				sfSimpleBlogPostVersionPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded

			$key2 = sfSimpleBlogPostPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = sfSimpleBlogPostPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = sfSimpleBlogPostPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					sfSimpleBlogPostPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded

				// Add the $obj1 (sfSimpleBlogPostVersion) to $obj2 (sfSimpleBlogPost)
				$obj2->addsfSimpleBlogPostVersion($obj1);

			} // if joined row was not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
