	public static function retrieveByPK($id, $version, PropelPDO $con = null) {
		$_instancePoolKey = serialize(array((string) $id, (string) $version));
 		if (null !== ($obj = sfSimpleBlogPostVersionPeer::getInstanceFromPool($_instancePoolKey))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(sfSimpleBlogPostVersionPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(sfSimpleBlogPostVersionPeer::DATABASE_NAME);
		$criteria->add(sfSimpleBlogPostVersionPeer::ID, $id);
		$criteria->add(sfSimpleBlogPostVersionPeer::VERSION, $version);
		$v = sfSimpleBlogPostVersionPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
